SELECT gi.talk_id, 1 as talkType, null as avatarUrl, gur.group_id IFNULL(gur.group_name_remark, gi.group_name) as talkName FROM ( SELECT id, group_name, talk_id FROM group_info WHERE talk_id IN
#{item}
) gi LEFT JOIN group_user_relation gur ON gi.id = gur.group_id WHERE gur.user_id = #{userId}
