SELECT ur.talk_id, 0 as talkType, ui.avatar_url, IFNULL(ur.sub_nickname_remark, ui.nickname) as talkName FROM ( SELECT main_user_id, sub_user_id, talk_id, sub_nickname_remark FROM user_relation WHERE main_user_id = #{userId} AND talk_id IN
#{item}
) ur LEFT JOIN user_info ui ON ur.sub_user_id = ui.id
